pragma solidity ^0.6.6;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

contract Staking is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    uint256 public constant BLOCKS_IN_YEAR = 2102400; // if one block per 15 seconds
    mapping(address => Staker) private _stakers;

    address private _rewardToken;
    address private _stakingToken;
    uint256 private _startsAt;
    uint256 private _durationInBlocks;
    uint256 private _finishBlock;
    uint256 private _lpLockTime;
    uint256 private _numberOfStakers;
    uint256 private _lpTotalAmount;
    uint256 private _lpTotalLimit;
    uint256 private _stakersLimit;
    uint256 private _maxStakePerStaker;
    uint256 private _rewardsPerBlock;
    uint256 private _rewardPerTokenStored;
    uint256 private _lastUpdateBlock;
    bool private _isActive;

    struct Staker {
        address stakerAddress;
        uint256 balance;
        uint256 rewards;
        uint256 rewardPerTokenPaid;
        uint256 startBlock;
        uint256 startTime;
    }

    function getUserInfo(address userAddress) external view returns (Staker memory) {
        return _stakers[userAddress];
    }

    function getStakeInfo()
        external
        view
        returns (uint256, uint256, address, address, uint256, uint256, uint256, uint256, bool)
    {
        return (
            _startsAt,
            _finishBlock,
            _rewardToken,
            _stakingToken,
            _lpTotalAmount,
            _numberOfStakers,
            _rewardsPerBlock,
            _lastUpdateBlock,
            _isActive
        );
    }

    function getStakeConditionsInfo() external view returns (uint256, uint256, uint256, uint256, uint256, uint256) {
        return (
            _lpLockTime,
            _lpTotalLimit,
            _stakersLimit,
            _maxStakePerStaker,
            _rewardPerTokenStored,
            _durationInBlocks
        );
    }

    function getTokenBalance(address token) public view returns (uint256) {
        if (token == address(0)) {
            return address(this).balance;
        } else {
            return IERC20(token).balanceOf(address(this));
        }
    }

    function earned(address stakerAddress) public view returns (uint256) {
        Staker memory staker = _stakers[stakerAddress];
        uint256 rewardPerBlockNow = rewardPerToken();
        return
            rewardPerBlockNow != 0
                ? ((staker.balance * (rewardPerBlockNow - staker.rewardPerTokenPaid)) / 1 ether) + staker.rewards
                : 0 + staker.rewards;
    }

    event ActiveSet(bool indexed value, address operator);
    event EmergencyWithdrawals(address indexed stakerAddress, bool payHarvest, address indexed operator);
    event RewardsDeposited(address indexed tokenAddress, uint256 amount, address indexed operator);
    event RewardPerBlockSet(uint256 indexed newRewardPerBlock, address indexed operator);
    event StakeCreated(
        address rewardToken,
        address stackingToken,
        uint256 startsAt,
        uint256 lpLockTime,
        uint256 lpTotalLimit,
        uint256 stakersLimit,
        uint256 maxStakePerStaker,
        uint256 rewardsPerBlock,
        uint256 durationInBlocks
    );
    event StakeUpdated(
        uint256 startsAt,
        uint256 blocksDuration,
        uint256 lpLockTime,
        uint256 stakersLimit,
        uint256 maxStakePerStaker,
        uint256 indexed newRewardPerBlock,
        address indexed operator
    );
    event MaxStakePerStakerSet(uint256 newMaxStakePerStaker, address indexed operator);
    event Withdrawed(uint256 amount, address indexed operator);
    event TokensStaked(uint256 amount, address indexed operator);
    event Harvested(uint256 amount, address indexed operator);
    event TokenWithdrawed(uint256 amount, address indexed operator);

    constructor(
        address rewardToken,
        address stackingToken,
        uint256 startsAt,
        uint256 durationInBlocks,
        uint256 lpLockTime,
        uint256 stakersLimit,
        uint256 maxStakePerStaker,
        uint256 lpTotalLimit,
        uint256 rewardsPerBlock
    ) public {
        _rewardToken = rewardToken;
        _stakingToken = stackingToken;
        _startsAt = startsAt;
        _finishBlock = block.number + durationInBlocks;
        _lpLockTime = lpLockTime;
        _lpTotalLimit = lpTotalLimit;
        _stakersLimit = stakersLimit;
        _maxStakePerStaker = maxStakePerStaker;
        _rewardsPerBlock = rewardsPerBlock;
        _durationInBlocks = durationInBlocks;
        _isActive = true;
        emit StakeCreated(
            rewardToken,
            stackingToken,
            startsAt,
            lpLockTime,
            lpTotalLimit,
            stakersLimit,
            maxStakePerStaker,
            rewardsPerBlock,
            durationInBlocks
        );
    }

    receive() external payable {}

    function emergencyWithdrawal(
        address stakerAddress,
        bool payHarvest
    ) external onlyOwner nonReentrant updateReward(stakerAddress) {
        Staker storage staker = _stakers[stakerAddress];
        if (payHarvest) {
            _withdrawHarvest(staker);
        }
        _withdrawLP(staker, staker.balance);
        emit EmergencyWithdrawals(stakerAddress, payHarvest, _msgSender());
    }

    /*
    withdraw only reward
  */
    function harvest() external nonReentrant updateReward(_msgSender()) {
        _withdrawHarvest(_stakers[_msgSender()]);
    }

    function stakeTokens(uint256 amount) external payable nonReentrant updateReward(_msgSender()) {
        Staker storage staker = _stakers[_msgSender()];
        _stake(staker, amount);
    }

    function setActive(bool value) external onlyOwner {
        _isActive = value;
        emit ActiveSet(value, _msgSender());
    }

    function setRewardPerBlock(uint256 _newRewardPerBlock) public onlyOwner {
        _rewardsPerBlock = _newRewardPerBlock;
        emit RewardPerBlockSet(_newRewardPerBlock, _msgSender());
    }

    function updateStake(
        uint256 newStartsAt,
        uint256 newBlocksDuration,
        uint256 newLpLockTime,
        uint256 newStakersLimit,
        uint256 newMaxStakePerStaker,
        uint256 newRewardPerBlock
    ) external onlyOwner {
        if (block.timestamp < _startsAt) {
            _lpLockTime = newLpLockTime;
            _startsAt = newStartsAt;
        }
        if (block.timestamp < _startsAt || _lpLockTime > newLpLockTime) {
            _lpLockTime = newLpLockTime;
        }
        _finishBlock = block.number + newBlocksDuration;
        _durationInBlocks = newBlocksDuration;
        _stakersLimit = newStakersLimit;
        _maxStakePerStaker = newMaxStakePerStaker;
        _rewardsPerBlock = newRewardPerBlock;
        emit StakeUpdated(
            _startsAt,
            newBlocksDuration,
            _lpLockTime,
            newStakersLimit,
            newMaxStakePerStaker,
            newRewardPerBlock,
            _msgSender()
        );
    }

    /**
     * Dev: withdraw both lp and reward tokens
     **/
    function withdraw(uint256 amount) external nonReentrant updateReward(_msgSender()) {
        Staker storage staker = _stakers[_msgSender()];
        // _withdrawHarvest(staker);
        _withdrawLP(staker, amount);
    }

    /**
     * Dev: owner or Creator can withdrawal Rewards tokens.
     **/
    function withdrawTokens(address token, uint256 amount) external nonReentrant onlyOwner {
        if (block.number < _finishBlock + BLOCKS_IN_YEAR)
            require(token != _stakingToken, "Staking: can not withdraw stacking tokens before lock time");
        sendTokens(_msgSender(), token, amount);
        emit TokenWithdrawed(amount, _msgSender());
    }

    function rewardPerToken() public view returns (uint256) {
        uint256 lastUpdateBlock = _lastUpdateBlock;
        uint256 finishBlock = _finishBlock;
        if (_lpTotalAmount == 0) return 0; // TODO: check with rewardPerTokenStored not 0
        uint256 lastBlock = block.number > finishBlock ? finishBlock : block.number;
        uint256 rewardedBlocks = lastUpdateBlock < lastBlock ? lastBlock - lastUpdateBlock : 0;
        return (_rewardPerTokenStored + ((_rewardsPerBlock * rewardedBlocks * 1 ether) / _lpTotalAmount));
    }

    function _stake(Staker storage staker, uint256 amount) internal {
        require(amount > 0, "Staking: Amount must be greater than zero");
        require(_isActive, "Staking: This stake is inactive or not exist");
        require(block.timestamp >= _startsAt, "Staking: This staking has not started yet");
        require(block.number <= _finishBlock, "Staking: This staking is currently closed");
        if (_stakersLimit != 0) require(_numberOfStakers < _stakersLimit, "Staking: This stake is already full");
        if (_lpTotalLimit != 0)
            require(_lpTotalAmount + amount <= _lpTotalLimit, "Staking: This stake is already full");
        if (_maxStakePerStaker != 0)
            require(staker.balance + amount <= _maxStakePerStaker, "Staking: Your total stake size is too big");
        _lpTotalAmount += amount;
        staker.balance += amount;
        if (_stakingToken == address(0)) {
            require(msg.value >= amount, "Staking: Wrong amount");
            if (msg.value > amount) sendTokens(_msgSender(), _stakingToken, msg.value - amount);
        } else {
            IERC20(_stakingToken).safeTransferFrom(_msgSender(), address(this), amount);
        }
        if (staker.startBlock == 0) {
            _numberOfStakers += 1;
            staker.startBlock = block.number;
            staker.startTime = block.timestamp;
            staker.stakerAddress = _msgSender();
        }
        emit TokensStaked(amount, staker.stakerAddress);
    }

    function _withdrawLP(Staker storage staker, uint256 amount) internal {
        require(amount <= staker.balance, "Exceeds balance");
        // uint256 amount = staker.balance;
        require(amount > 0, "Balance must be greater than zero");
        if (_msgSender() != owner()) {
            require(
                block.timestamp >= staker.startTime + _lpLockTime || block.number > _finishBlock,
                "Staking: Too early for withdraw"
            );
        }
        _lpTotalAmount -= amount;
        if (amount == staker.balance) {
            _numberOfStakers -= 1;
            staker.startBlock = 0;
            staker.startTime = 0;
            staker.balance = 0;
        } else {
            staker.balance -= amount;
        }

        sendTokens(staker.stakerAddress, _stakingToken, amount);
        emit Withdrawed(amount, staker.stakerAddress);
    }

    function _withdrawHarvest(Staker storage staker) internal {
        require(staker.startBlock != 0, "Staking: You are not a staker");
        uint256 reward = staker.rewards;
        if (reward > 0) {
            staker.rewards = 0;
            staker.startBlock = block.number;
            sendTokens(staker.stakerAddress, _rewardToken, reward);
        }
        emit Harvested(reward, staker.stakerAddress);
    }

    modifier updateReward(address stakerAddress) {
        Staker storage staker = _stakers[stakerAddress];
        _rewardPerTokenStored = rewardPerToken();
        _lastUpdateBlock = block.number < _finishBlock ? block.number : _finishBlock;
        staker.rewards = earned(stakerAddress);
        staker.rewardPerTokenPaid = _rewardPerTokenStored;
        _;
    }

    /**
     * @dev Function is used to transfer native tokens from contract.
     *
     * @param to is address of recipient.
     * @param amount of native tokens to transfer.
     **/
    function sendTokens(address to, address token, uint256 amount) private returns (bool) {
        require(getTokenBalance(token) >= amount, "Staking: not enough tokens");
        if (token == address(0)) {
            (bool sent, ) = payable(to).call{value: amount}("");
            require(sent, "GameStake: failed to send Tokens");
            return true;
        } else {
            IERC20(token).safeTransfer(_msgSender(), amount);
            return true;
        }
    }
}
